import { useEffect, useState } from "react";
import axios from "axios";
import "./rightBar.scss";

const RightBar = () => {
  const [followableUsers, setFollowableUsers] = useState([]);

  useEffect(() => {
    const fetchFollowableUsers = async () => {
      try {
        const res = await axios.get("http://localhost:8800/api/users/followable", {
          withCredentials: true,
        });
        setFollowableUsers(res.data);
        console
      } catch (err) {
        console.error(err);
      }
    };

    fetchFollowableUsers();
  }, []);

  const handleFollow = async (userId) => {
    try {
      await axios.post("http://localhost:8800/api/follow", { userId }, {
        withCredentials: true,
      });
      setFollowableUsers((prev) => prev.filter((user) => user.id !== userId));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="rightBar">
      <div className="container">
        <div className="item">
          <span>Suggestions For You</span>
          {followableUsers.map((user) => (
            <div className="user" key={user.id}>
              <div className="userInfo">
                <img src={user.profilePic || "default_profile_pic_url"} alt="" />
                <span>{user.username}</span>
              </div>
              <div className="buttons">
                <button onClick={() => handleFollow(user.id)}>Follow</button>
                <button>Dismiss</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RightBar;
