import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import { AuthContext } from "../../context/authContext";
import { useContext } from "react";
import "./followersList.scss";

const RightBar = () => {
  const { currentUser } = useContext(AuthContext);
  const queryClient = useQueryClient();

  // Fetch all users with follow status
  const { isLoading, error, data } = useQuery(["users"], () =>
    makeRequest.get("/users").then((res) => {
      return res.data;
    })
  );

  // Mutation to follow/unfollow users
  const mutation = useMutation(
    ({ userId, following }) => {
      if (following) {
        return makeRequest.delete(`/relationships?userId=${userId}`);
      } else {
        return makeRequest.post("/relationships", { userId });
      }
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(["users"]); // Refetch users list to update follow status
      },
    }
  );

  const handleFollow = (userId, following) => {
    mutation.mutate({ userId, following });
  };

  if (isLoading) return "Loading...";
  if (error) return "Something went wrong!";

  return (
    <div className="followersList">
      {data.map((user) => (
        <div key={user.id} className="userItem">
          <img src={"/upload/" + user.profilePic} alt="Profile" className="profilePic" />
          <span>{user.username}</span>
          {user.id !== currentUser.id && (
            <button onClick={() => handleFollow(user.id, user.isFollowing)}>
              {user.isFollowing ? "Unfollow" : "Follow"}
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default FollowersList;
