import React from 'react';
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { makeRequest } from "../../axios";

const RightBar = () => {
  const queryClient = useQueryClient();

  // Fetch users and follow status
  const { isLoading, error, data } = useQuery(["usersWithFollowStatus"], () =>
    makeRequest.get("/users-with-follow-status").then((res) => res.data)
  );

  // Mutation for follow/unfollow
  const followMutation = useMutation(
    (userId) => makeRequest.post("/relationships", { userId }),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(["usersWithFollowStatus"]);
      },
    }
  );

  const unfollowMutation = useMutation(
    (userId) => makeRequest.delete(`/relationships?userId=${userId}`),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(["usersWithFollowStatus"]);
      },
    }
  );

  const handleFollow = (userId, isFollowing) => {
    if (isFollowing) {
      unfollowMutation.mutate(userId);
    } else {
      followMutation.mutate(userId);
    }
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error loading users</div>;

  return (
    <div className="usersList">
      {data.map((user) => (
        <div className="user" key={user.id}>
          <img src={`/upload/${user.profilePic}`} alt={user.name} />
          <div>
            <span>{user.name}</span>
            <button onClick={() => handleFollow(user.id, user.isFollowing)}>
              {user.isFollowing ? "Unfollow" : "Follow"}
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default UsersList;
